function mysqlReadAll($query) {
    global $conn;
    
    $result = mysqli_query($conn, $query);
    if ($result->num_rows < 1) {
        return array();
    }
    // Fetch all
    $data = mysqli_fetch_all($result, MYSQLI_ASSOC);
